$.AddBallsLinear = function (start, size, padding, count, balls) {    
    if (count < 3) { count = 3; }

    var x = start.X;
    
    for (var i = 0; i < count; i++) {       
        balls.push(new $.Ball(new $.Rectangle(x, start.Y, size.X, size.Y), $.BallIndex));
        x += size.X + padding.X;

        $.BallIndex++;
        if ($.BallIndex > 4) { $.BallIndex = 0; }
    }
};

$.AddBallsDiaganol = function (start, size, padding, count, balls, up) {
    if (count < 3) { count = 3; }

    var x = start.X;
    var y = start.Y;

    for (var i = 0; i < count; i++) {        
        balls.push(new $.Ball(new $.Rectangle(x, y, size.X, size.Y), $.BallIndex));
        x += size.X + padding.X;

        if (!up) { y += size.Y + padding.Y; }
        else { y -= size.Y + padding.Y; }

        $.BallIndex++;
        if ($.BallIndex > 4) { $.BallIndex = 0; }
    }

    return balls;
};

$.AddBallsCurve = function (start, size, padding, count, balls) {
    if (count < 3) { count = 3; }

    var x = start.X;
    var y = start.Y;

    for (var i = 0; i < count; i++) {        
        balls.push(new $.Ball(new $.Rectangle(x, y, size.X, size.Y), $.BallIndex));
        x += size.X + padding.X;

        if (i >= (count - 1) / 2) { y += size.Y + padding.Y; }
        else { y -= size.Y + padding.Y; }

        $.BallIndex++;
        if ($.BallIndex > 4) { $.BallIndex = 0; }
    }

    return balls;
};
